import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';

type Employee = {
  id: number;
  name: string;
};

function getEmployeeName(emp: Employee|null) {
  return emp?.name; // 👈️ use optional chaining
}

// 👇️ "Frank"
console.log(getEmployeeName(null));
