import React, { useRef } from 'react';

interface ShoppingListFormProps {
  onAddItem:(product:string)=> void;
}


const ShoppingListFrom = ({onAddItem}:ShoppingListFormProps) :JSX.Element => {

  const inputRef = useRef<HTMLInputElement>(null);
  const submitHandler = (e: React.FormEvent) => {
    e.preventDefault();
    onAddItem(inputRef.current!.value);
    inputRef.current.value = "";
  }

  return(
    <form onSubmit={submitHandler}>
    <input ref={inputRef} type="text" placeholder="item's name"/>
    <button type="submit"> Add Item </button>
    </form>
  )
} 

export default ShoppingListFrom;
